import { sql } from "drizzle-orm";
import { pgTable, text, varchar } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

// Request form schema for contact/service requests
export const requestFormSchema = z.object({
  name: z.string().min(2, "–ò–º—è –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å –º–∏–Ω–∏–º—É–º 2 —Å–∏–º–≤–æ–ª–∞"),
  phone: z.string().min(10, "–£–∫–∞–∂–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä —Ç–µ–ª–µ—Ñ–æ–Ω–∞"),
  city: z.string().min(2, "–£–∫–∞–∂–∏—Ç–µ –≥–æ—Ä–æ–¥"),
  address: z.string().min(5, "–£–∫–∞–∂–∏—Ç–µ –ø–æ–ª–Ω—ã–π –∞–¥—Ä–µ—Å"),
  apartment: z.string().optional(),
  message: z.string().optional(),
});

export type RequestFormData = z.infer<typeof requestFormSchema>;

// OneSignal types for admin panel
export interface OneSignalSubscriber {
  id: string;
  identifier?: string;
  tags?: {
    name?: string;
    phone?: string;
    city?: string;
    address?: string;
    message?: string;
  };
  last_active?: number;
  notification_types?: number;
}

export interface NotificationRequest {
  subscriberId: string;
  message: string;
