import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { adminAuthSchema, type AdminAuthData, type SupabaseRequest } from "@shared/schema";
import { supabaseAPI } from "@/lib/supabase";
import { oneSignalAPI } from "@/lib/onesignal-api";
import { useToast } from "@/hooks/use-toast";
import { Loader2, Lock, RefreshCw, CheckCircle, Truck, Clock, Calendar } from "lucide-react";
import { format } from "date-fns";
import { ru } from "date-fns/locale";

const ADMIN_PASSWORD = "admin123";

export default function Admin() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [requests, setRequests] = useState<SupabaseRequest[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [sendingTo, setSendingTo] = useState<string | null>(null);
  const { toast } = useToast();

  const authForm = useForm<AdminAuthData>({
    resolver: zodResolver(adminAuthSchema),
    defaultValues: {
      password: "",
    },
  });

  useEffect(() => {
    const savedAuth = localStorage.getItem("admin_auth");
    if (savedAuth === "true") {
      setIsAuthenticated(true);
      loadRequests();
    }
  }, []);

  const loadRequests = async () => {
    setIsLoading(true);
    try {
      const data = await supabaseAPI.getAllRequests();
      setRequests(data);
      toast({
        title: "–ó–∞—è–≤–∫–∏ –∑–∞–≥—Ä—É–∂–µ–Ω—ã",
        description: `–ù–∞–π–¥–µ–Ω–æ: ${data.length} –∑–∞—è–≤–æ–∫`,
      });
    } catch (error) {
      toast({
        variant: "destructive",
        title: "–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–ø–∏—Å–æ–∫ –∑–∞—è–≤–æ–∫",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const onLogin = (data: AdminAuthData) => {
    if (data.password === ADMIN_PASSWORD) {
      setIsAuthenticated(true);
      localStorage.setItem("admin_auth", "true");
      loadRequests();
      toast({
        title: "–í—Ö–æ–¥ –≤—ã–ø–æ–ª–Ω–µ–Ω",
        description: "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ –∞–¥–º–∏–Ω-–ø–∞–Ω–µ–ª—å",
      });
    } else {
      toast({
        variant: "destructive",
        title: "–ù–µ–≤–µ—Ä–Ω—ã–π –ø–∞—Ä–æ–ª—å",
        description: "–ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞",
      });
    }
  };

  const sendNotification = async (request: SupabaseRequest, status: string) => {
    if (!request.onesignal_id) {
      toast({
        variant: "destructive",
        title: "–ù–µ—Ç –ø–æ–¥–ø–∏—Å–∫–∏",
        description: "–ö–ª–∏–µ–Ω—Ç –Ω–µ –ø–æ–¥–ø–∏—Å–∞–ª—Å—è –Ω–∞ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è",
      });
      return;
    }

    setSendingTo(request.id);
    try {
      let heading = "";
      let message = "";

      const name = request.name || "–ö–ª–∏–µ–Ω—Ç";
      const address = request.address || "—É–∫–∞–∑–∞–Ω–Ω—ã–π –∞–¥—Ä–µ—Å";

      switch (status) {
        case "processing":
          heading = "–ó–∞—è–≤–∫–∞ –≤ –æ–±—Ä–∞–±–æ—Ç–∫–µ";
          message = `${name}, –≤–∞—à–∞ –∑–∞—è–≤–∫–∞ –ø—Ä–∏–Ω—è—Ç–∞ –≤ —Ä–∞–±–æ—Ç—É. –ú—ã —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è.`;
          break;
        case "departed":
          heading = "–ú–∞—Å—Ç–µ—Ä –≤—ã–µ—Ö–∞–ª";
          message = `${name}, –º–∞—Å—Ç–µ—Ä –≤—ã–µ—Ö–∞–ª –∫ –≤–∞–º –ø–æ –∞–¥—Ä–µ—Å—É: ${address}`;
          break;
        case "solved":
          heading = "–ü—Ä–æ–±–ª–µ–º–∞ —Ä–µ—à–µ–Ω–∞";
          message = `${name}, —Ä–∞–±–æ—Ç–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞. –°–ø–∞—Å–∏–±–æ –∑–∞ –æ–±—Ä–∞—â–µ–Ω–∏–µ! –ö–∞–∫ –≤–∞–º –Ω–∞—à–µ –æ–±—Å–ª—É–∂–∏–≤–∞–Ω–∏–µ?`;
          break;
      }

      await oneSignalAPI.sendNotification({
        subscriberId: request.onesignal_id,
        heading,
        message,
      });

      toast({
        title: "–£–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ",
        description: `${heading} ‚Üí ${request.name || request.phone}`,
      });
    } catch (error) {
      toast({
        variant: "destructive",
        title: "–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –æ—Ç–ø—Ä–∞–≤–∏—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ",
      });
    } finally {
      setSendingTo(null);
    }
  };

  const logout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem("admin_auth");
    setRequests([]);
    authForm.reset();
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background p-4">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Lock className="h-5 w-5" />
              –í—Ö–æ–¥ –≤ –∞–¥–º–∏–Ω-–ø–∞–Ω–µ–ª—å
            </CardTitle>
            <CardDescription>
              –í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å –¥–ª—è –¥–æ—Å—Ç—É–ø–∞ –∫ –ø–∞–Ω–µ–ª–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è–º–∏
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...authForm}>
              <form onSubmit={authForm.handleSubmit(onLogin)} className="space-y-4">
                <FormField
                  control={authForm.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>–ü–∞—Ä–æ–ª—å</FormLabel>
                      <FormControl>
                        <Input
                          type="password"
                          placeholder="–í–≤–µ–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å"
                          data-testid="input-password"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <Button type="submit" className="w-full" data-testid="button-login">
                  –í–æ–π—Ç–∏
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="border-b">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold">–ê–¥–º–∏–Ω-–ø–∞–Ω–µ–ª—å</h1>
            <p className="text-sm text-muted-foreground">–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è–º–∏</p>
          </div>
          <div className="flex gap-2">
            <Button
              variant="outline"
              onClick={loadRequests}
              disabled={isLoading}
              data-testid="button-refresh"
            >
              <RefreshCw className={`h-4 w-4 mr-2 ${isLoading ? "animate-spin" : ""}`} />
              –û–±–Ω–æ–≤–∏—Ç—å
            </Button>
            <Button variant="outline" onClick={logout} data-testid="button-logout">
              –í—ã—Ö–æ–¥
            </Button>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        <Card>
          <CardHeader>
            <CardTitle>–°–ø–∏—Å–æ–∫ –∑–∞—è–≤–æ–∫ ({requests.length})</CardTitle>
            <CardDescription>
              –í—Å–µ –∑–∞—è–≤–∫–∏ –∫–ª–∏–µ–Ω—Ç–æ–≤ —Å –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å—é –æ—Ç–ø—Ä–∞–≤–∫–∏ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π
            </CardDescription>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center py-12">
                <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
              </div>
            ) : requests.length === 0 ? (
              <div className="text-center py-12 text-muted-foreground">
                –ó–∞—è–≤–æ–∫ –ø–æ–∫–∞ –Ω–µ—Ç
              </div>
            ) : (
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>–î–∞—Ç–∞</TableHead>
                      <TableHead>–ò–º—è</TableHead>
                      <TableHead>–¢–µ–ª–µ—Ñ–æ–Ω</TableHead>
                      <TableHead>–ì–æ—Ä–æ–¥</TableHead>
                      <TableHead>–ê–¥—Ä–µ—Å</TableHead>
                      <TableHead>–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π</TableHead>
                      <TableHead>–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è</TableHead>
                      <TableHead className="text-right">–î–µ–π—Å—Ç–≤–∏—è</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {requests.map((request) => (
                      <TableRow key={request.id}>
                        <TableCell className="text-sm text-muted-foreground" data-testid={`text-date-${request.id}`}>
                          <div className="flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            {format(new Date(request.created_at), "dd.MM.yyyy HH:mm", { locale: ru })}
                          </div>
                        </TableCell>
                        <TableCell className="font-medium" data-testid={`text-name-${request.id}`}>
                          {request.name}
                        </TableCell>
                        <TableCell data-testid={`text-phone-${request.id}`}>
                          {request.phone}
                        </TableCell>
                        <TableCell data-testid={`text-city-${request.id}`}>
                          {request.city}
                        </TableCell>
                        <TableCell data-testid={`text-address-${request.id}`}>
                          {request.address}
                          {request.apartment && `, –∫–≤. ${request.apartment}`}
                        </TableCell>
                        <TableCell className="max-w-xs truncate" data-testid={`text-message-${request.id}`}>
                          {request.message || "‚Äî"}
                        </TableCell>
                        <TableCell>
                          {request.onesignal_id ? (
                            <Badge variant="default" className="text-xs">
                              –í–∫–ª—é—á–µ–Ω—ã
                            </Badge>
                          ) : (
                            <Badge variant="secondary" className="text-xs">
                              –ù–µ—Ç
                            </Badge>
                          )}
                        </TableCell>
                        <TableCell className="text-right">
                          <div className="flex gap-2 justify-end flex-wrap">
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => sendNotification(request, "processing")}
                              disabled={!request.onesignal_id || sendingTo === request.id}
                              data-testid={`button-processing-${request.id}`}
                            >
                              {sendingTo === request.id ? (
                                <Loader2 className="h-3 w-3 animate-spin" />
                              ) : (
                                <Clock className="h-3 w-3 mr-1" />
                              )}
                              –í –æ–±—Ä–∞–±–æ—Ç–∫–µ
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => sendNotification(request, "departed")}
                              disabled={!request.onesignal_id || sendingTo === request.id}
                              data-testid={`button-departed-${request.id}`}
                            >
                              {sendingTo === request.id ? (
                                <Loader2 className="h-3 w-3 animate-spin" />
                              ) : (
                                <Truck className="h-3 w-3 mr-1" />
                              )}
                              –ú–∞—Å—Ç–µ—Ä –≤—ã–µ—Ö–∞–ª
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => sendNotification(request, "solved")}
                              disabled={!request.onesignal_id || sendingTo === request.id}
                              data-testid={`button-solved-${request.id}`}
                            >
                              {sendingTo === request.id ? (
                                <Loader2 className="h-3 w-3 animate-spin" />
                              ) : (
                                <CheckCircle className="h-3 w-3 mr-1" />
                              )}
                              –†–µ—à–µ–Ω–∞
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

